import 'dart:math';class AppMethod {  static String generateRandomColor() {    final Random random = Random();    List<String> colorOptions = [      '0xFF7F8C8D',      '0xFF2980B9',      '0xFF8E44AD',      '0xFFF1C40F',      '0xFF27AE60',      '0xFFC0392B'    ];    return colorOptions[random.nextInt(colorOptions.length)];  }  static String generateAlphanumericID() {    final Random random = Random();    const String chars =        r'abcdefghijklmnopqrstuvwxyzABCD+-/$^!@#EFGHIJKLMNOPQRSTUVWXYZ0123456789';    return List.generate(6, (index) => chars[random.nextInt(chars.length)])        .join();  }}