import 'package:flutter/material.dart';import '../notes.dart';import '../../../Features/Main/data/models/notes_model.dart';class ListViewNotes extends StatefulWidget {  final List<NotesModel> notes;  const ListViewNotes({    super.key,    required this.notes,  });  @override  State<ListViewNotes> createState() => _ListViewNotesState();}class _ListViewNotesState extends State<ListViewNotes> {  late final GlobalKey<AnimatedListState> keyList;  @override  void initState() {    super.initState();    keyList = GlobalKey<AnimatedListState>();  }  @override  Widget build(BuildContext context) {    if (widget.notes.isEmpty) {      return const Center(child: Text('No notes available'));    }    return AnimatedList(        initialItemCount: widget.notes.length,        key: keyList,        itemBuilder: (context, index, animation) {          return Notes(note: widget.notes[index]);        });  }}