import 'package:flutter/material.dart';import '../../../Features/Main/data/models/notes_model.dart';import '../notes.dart';class ListViewNotes extends StatefulWidget {  final List<NotesModel> notes;  final Duration animationDuration;  const ListViewNotes({    super.key,    required this.notes,    this.animationDuration = const Duration(milliseconds: 500),  });  @override  State<ListViewNotes> createState() => _ListViewNotesState();}class _ListViewNotesState extends State<ListViewNotes> {  late final GlobalKey<AnimatedListState> keyList;  @override  void initState() {    super.initState();    keyList = GlobalKey<AnimatedListState>();  }  @override  Widget build(BuildContext context) {    if (widget.notes.isEmpty) {      return const Center(child: Text('No notes available'));    }    return AnimatedList(      key: keyList,      initialItemCount: widget.notes.length,      itemBuilder: (context, index, animation) {        return Notes(note: widget.notes[index]);      },    );  }  void removeNoteAtIndex(int index) {    // final removedNote = widget.notes.removeAt(index);    keyList.currentState?.removeItem(      index,      (context, animation) => const SizedBox.shrink(),      duration: const Duration(milliseconds: 300),    );  }}