import 'package:flutter/material.dart';class DialogExample extends StatelessWidget {  final String title;  final String content;  final VoidCallback? function;  const DialogExample(      {super.key,      required this.title,      required this.content,       this.function});  @override  Widget build(BuildContext context) {    return AlertDialog(      title: Text(title),      content: Text(content),      actions: [        TextButton(          onPressed: () => Navigator.pop(            context,          ),          child: const Text('Cancel'),        ),        TextButton(          onPressed: function,          child: const Text('OK'),        ),      ],    );  }}