import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../Features/Main/presentation/manager/cubit.dart';import '../../Features/Main/presentation/manager/states.dart';import 'loading.dart';class AllNotesView extends StatelessWidget {   final int item;  final NullableIndexedWidgetBuilder child;  const AllNotesView({super.key, required this.item, required this.child});  @override  Widget build(BuildContext context) {    if (item == 0) {      return const Center(child: Text('No notes available'));    }    return BlocConsumer<AppCubit, AppState>(builder: (context, state) {      if (state is LoadingGetData) {        return const LoadingWidget();      } else if (state is ErrorGetData) {        //^ Massage of Error        return Text(state.text);      } else {        return ListView.separated(          separatorBuilder: (context, index) => const Divider(),          itemCount: item,          itemBuilder: child,        );      }    }, listener: (context, state) {    });  }}