import 'package:conditional_builder_null_safety/conditional_builder_null_safety.dart';import 'package:flutter/material.dart';extension ForApp on Widget {  Widget loadingScreen(bool condition, Widget builder) => ConditionalBuilder(        builder: (context) => builder,        condition: condition,        fallback: (context) => const Center(child: CircularProgressIndicator()),      );}class LoadingWidget extends StatelessWidget {  const LoadingWidget({super.key});  @override  Widget build(BuildContext context) {    return const Center(child: CircularProgressIndicator());  }}