import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:intl/intl.dart';import '../../Features/Main/data/models/notes_model.dart';import '../../Features/Main/presentation/manager/cubit.dart';import '../../Features/Main/presentation/manager/states.dart';import '../Resources/icon.dart';class Notes extends StatelessWidget {  final NotesModel note;  final int index;  const Notes({super.key, required this.note, required this.index});  @override  Widget build(BuildContext context) {    Color noteColor = Color(int.parse(note.color));    return BlocBuilder<AppCubit, AppState>(      builder: (context, state) {        return Padding(          padding: const EdgeInsets.all(8.0),          child: SizedBox(            width: double.infinity,            child: Card(                color: noteColor,                child: Padding(                  padding: const EdgeInsets.all(10.0),                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      Row(                        mainAxisAlignment: MainAxisAlignment.spaceBetween,                        children: [                          Text(note.title,                              style: Theme.of(context).textTheme.bodyLarge),                          note.favorite == 'yes' ||                                  AppCubit.get(context).favorites[note.id]!                              ? AppIcons.iconStar                              : Container()                        ],                      ),                      Padding(                        padding: const EdgeInsets.all(5.0),                        child: Text(note.field,                            style: Theme.of(context).textTheme.bodyMedium),                      ),                      const SizedBox(                        height: 8,                      ),                      Text('${formatDate2(note.date)}  ${note.time}',                          style: Theme.of(context).textTheme.bodySmall),                    ],                  ),                )),          ),        );      },    );  }}String formatDate2(String dateTime2) {  // final DateTime dateTime = DateTime.now();  DateTime dateTime = DateTime.parse(dateTime2);  final day = dateTime.day;  final month = DateFormat.MMMM().format(dateTime);  final year = dateTime.year;  return "$day $month $year";}