import 'package:note_pulse/Core/Network/SqLite/strings_sql.dart';class NotesModel {  late String id;  late String title;  late String field;  late String color;  late String time;  late String date;  late String createdAt;  late String favorite;  late String done;  NotesModel({    required this.id,    required this.title,    required this.field,    required this.color,    required this.time,    required this.date,    required this.createdAt,    required this.favorite,    required this.done,  });  NotesModel.fromjson(Map<String, dynamic> json) {    id = json[AppDatabaseHelper.rawId];    title = json[AppDatabaseHelper.rawTitle];    field = json[AppDatabaseHelper.rawField];    color = json[AppDatabaseHelper.rawColor];    time = json[AppDatabaseHelper.rawTime];    date = json[AppDatabaseHelper.rawDate];    createdAt = json[AppDatabaseHelper.rawCreateIN];    favorite = json[AppDatabaseHelper.rawFavorite];    done = json[AppDatabaseHelper.rawDone];  }  Map<String, dynamic> toMap() {    return {      AppDatabaseHelper.rawId: id,      AppDatabaseHelper.rawTime: time,      AppDatabaseHelper.rawDate: date,      AppDatabaseHelper.rawTitle: title,      AppDatabaseHelper.rawField: field,      AppDatabaseHelper.rawColor: color,      AppDatabaseHelper.rawFavorite: favorite,      AppDatabaseHelper.rawDone: done,      AppDatabaseHelper.rawCreateIN: createdAt,    };  }  NotesModel copyWith({    String? id,    String? title,    String? field,    String? color,    String? time,    String? date,    String? createdAt,    String? favorite,    String? done,  }) {    return NotesModel(      id: id ?? this.id,      title: title ?? this.title,      field: field ?? this.field,      color: color ?? this.color,      time: time ?? this.time,      date: date ?? this.date,      createdAt: createdAt ?? this.createdAt,      favorite: favorite ?? this.favorite,      done: done ?? this.done,    );  }}