import 'package:note_pulse/Core/Resources/string.dart';class NotesModel {  late String id;  late String title;  late String field;  late String color;  late String time;  late String date;  late String createdAt;  late String favorite;  late String done;  NotesModel({    required this.id,    required this.title,    required this.field,    required this.color,    required this.time,    required this.date,    required this.createdAt,    required this.favorite,    required this.done,  });  NotesModel.fromjson(Map<String, dynamic> json) {    id = json[AppDatabase.rawId];    title = json[AppDatabase.rawTitle];    field = json[AppDatabase.rawField];    color = json[AppDatabase.rawColor];    time = json[AppDatabase.rawTime];    date = json[AppDatabase.rawDate];    createdAt = json[AppDatabase.rawCreateIN];    favorite = json[AppDatabase.rawFavorite];    done = json[AppDatabase.rawDone];  }  Map<String, dynamic> toMap() {    return {      'id': id,      'title': title,      'content': field,      'color': color,      'time': time,      'date': date,      'createdAt': createdAt,      'favorite': favorite,      'done': done,    };  }}