import 'package:flutter/material.dart';import '../../../../Core/Widgets/custom_animated_list_item.dart';import '../../../../Core/Widgets/notes.dart';import '../../../../Core/Widgets/custom_view_list.dart';import '../manager/cubit.dart';class DoneScreen extends StatelessWidget {  const DoneScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(),      body: SafeArea(        child: ListViewNotes(            item: AppCubit.get(context).dataDone.length,            child: (context, index) => AnimatedListItem(                  index: index,                  note: AppCubit.get(context).dataDone[index],                  child: Notes(                    index: index,                    note: AppCubit.get(context).dataDone[index],                  ),                )),      ),    );  }}