import 'package:flutter/material.dart';class DefaultButton extends StatelessWidget {  final void Function() press;  final String text;  final Icon icon;  final double? width;  final Color background;  final double? fontSize;  final EdgeInsetsGeometry? padding;  const DefaultButton({    super.key,    required this.press,    required this.text,    this.width,    this.background = Colors.transparent,    this.fontSize,    this.padding,    required this.icon,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 8),      child: Container(        height: 80,        width: width ?? double.infinity,        color: background,        child: MaterialButton(          padding: const EdgeInsets.symmetric(horizontal: 30),          onPressed: press,          child: Row(            children: [              icon,              const SizedBox(                width: 20,              ),              Text(text, style: Theme.of(context).textTheme.bodyMedium,)            ],          ),        ),      ),    );  }}