import 'package:flutter/material.dart';import 'package:note_pulse/Features/Main/presentation/manager/main_cubit.dart';import '../../../../../Core/Resources/icon.dart';import '../../../../../Core/Resources/string.dart';import '../../../../../Core/generated/assets.dart';import '../../../../Setting/setting_screen.dart';import '../FloatingAction/floating_process.dart';import 'button.dart';Drawer drawer(context) => Drawer(      child: Column(        children: [          SizedBox(            height: 200,            child: Column(              mainAxisAlignment: MainAxisAlignment.center,              children: [                Image.asset(                  Assets.imagesIcLauncherForeground,                ),                Text(AppStrings.nameApp)              ],            ),          ),          DefaultButton(            press: () {              MainCubit.get(context).changeScreen(0);              pop(context);            },            text: AppStrings.notePage,            icon: AppIcons.iconNotesPage,          ),          DefaultButton(            press: () {              MainCubit.get(context).changeScreen(1);              pop(context);            },            text: AppStrings.favoritePage,            icon: AppIcons.iconFavoritePage,          ),          DefaultButton(            press: () {              MainCubit.get(context).changeScreen(2);              pop(context);            },            text: AppStrings.donePage,            icon: AppIcons.iconDonePage,          ),          DefaultButton(            press: () => nextScreen(context, const SettingScreen()),            text: AppStrings.settingPage,            icon: AppIcons.iconSettingPage,          ),          DefaultButton(            press: () {},            text: AppStrings.aboutPage,            icon: AppIcons.iconAboutPage,          ),        ],      ),    );