import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../manager/cubit.dart';import '../../manager/states.dart';import 'floating_process.dart';class CustomFloatingBar extends StatefulWidget {  const CustomFloatingBar({super.key});  @override  State<CustomFloatingBar> createState() => _CustomFloatingBarState();}class _CustomFloatingBarState extends State<CustomFloatingBar>    with SingleTickerProviderStateMixin {  late AnimationController _controller;  @override  void initState() {    super.initState();    _controller = AnimationController(vsync: this);  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return BlocConsumer<AppCubit, AppState>(      listener: (context, state) {      },      builder: (context, state) {        return FloatingActionButton(          shape: const CircleBorder(),            onPressed: () => pressButton(context),            child:            Icon(                AppCubit                .get(context)                .iconFloating));      },    );  }}