import 'package:flutter/material.dart';import 'package:note_pulse/Features/Main/data/models/notes_model.dart';import '../../../../../Core/Resources/method.dart';import '../../manager/cubit.dart';import '../../pages/screen_of_start.dart';import '../form_add_post.dart';var keyForm = GlobalKey<FormState>();TextEditingController title = TextEditingController();TextEditingController time = TextEditingController();TextEditingController date = TextEditingController();TextEditingController description = TextEditingController();//------------------------------------------------------------------------------------------------------------------------void removeTexts() {  time.text = '';  date.text = '';  title.text = '';  description.text = '';}void validateAndSubmit(context) {  if (keyForm.currentState?.validate() == true) {    final note = NotesModel(        id: AppMethod.generateAlphanumericID(),        title: title.text,        field: description.text,        color: AppMethod.generateRandomColor(),        time: time.text,        date: date.text,        createdAt: DateTime.now().toString(),        favorite: 'no',        done: 'no');    AppCubit.get(context).addNote(note);    Navigator.pop(context);  }}Future<void> showBottomSheet(context) async {  await keyScaffold.currentState      ?.showBottomSheet(        (context) => FormAddNote(          ketForm: keyForm,          time: time,          date: date,          name: title,          note: description,        ),      )      .closed      .then((value) {    AppCubit.get(context).changeBottomSheet(      iconData: Icons.post_add,      show: true,    );    removeTexts();  });}void pressButton(context) {  if (AppCubit.get(context).sheet == false) {    validateAndSubmit(context);  } else {    showBottomSheet(context);    AppCubit.get(context).changeBottomSheet(      iconData: Icons.add_circle_outline,      show: false,    );  }}void nextScreen(context, screen) {  Navigator.push(      context,      MaterialPageRoute(        builder: (context) => screen,      ));}void pop(context) {  Navigator.pop(context);}