import 'package:flutter/material.dart';import '../../manager/cubit.dart';import '../../pages/screen_of_start.dart';import '../form_add_post.dart';var keyForm = GlobalKey<FormState>();TextEditingController name = TextEditingController();TextEditingController time = TextEditingController();TextEditingController date = TextEditingController();TextEditingController post = TextEditingController();//------------------------------------------------------------------------------------------------------------------------void removeTexts() {  time.text = '';  date.text = '';  name.text = '';  post.text = '';}void validateAndSubmit(context) {  if (keyForm.currentState?.validate() == true) {    AppCubit.get(context).insertDatabase(      time: time.text,      date: date.text,      name: name.text,      post: post.text,    );    Navigator.pop(context);  }}void showBottomSheet(context) {  keyScaffold.currentState      ?.showBottomSheet(        (context) => FormAddNote(          ketForm: keyForm,          time: time,          date: date,          name: name,          note: post,        ),      )      .closed      .then((value) {    AppCubit.get(context).changeBottomSheet(      iconData: Icons.post_add,      show: true,    );    removeTexts();  });}void pressButton(context) {  if (AppCubit.get(context).sheet == false) {    validateAndSubmit(context);  } else {    showBottomSheet(context);    AppCubit.get(context).changeBottomSheet(      iconData: Icons.add_circle_outline,      show: false,    );  }}void nextScreen(context, screen) {  Navigator.push(      context,      MaterialPageRoute(        builder: (context) => screen,      ));}void pop(context) {  Navigator.pop(context);}