import 'package:flutter/material.dart';class DefaultTextFormField extends StatelessWidget {  final String text;  final bool? title;  final TextInputType type;  final TextEditingController? controller;  final IconData? prefix;  final Function()? pressSuffix;  final Function()? tap;  final String? Function(String?)? inputValidator;  final ValueChanged<String>? submitted;  final int? maxLine;  const DefaultTextFormField({    super.key,    required this.text,    required this.type,    this.controller,    this.prefix,    this.pressSuffix,    this.tap,    this.inputValidator,    this.submitted,    this.title,    this.maxLine,  });  @override  Widget build(BuildContext context) {    return TextFormField(      style: Theme.of(context).textTheme.bodyMedium,      autocorrect: true,      autofocus: true,      maxLines: maxLine ?? 1,      onFieldSubmitted: submitted,      controller: controller,      onTap: tap,      keyboardType: type,      validator: inputValidator,      decoration: InputDecoration(        labelStyle: Theme.of(context).textTheme.labelLarge,        alignLabelWithHint: true,        labelText: text,        prefixIcon: prefix != null ? Icon(prefix) : null,        border: OutlineInputBorder(borderRadius: BorderRadius.circular(20)),      ),    );  }}