import 'package:flutter/material.dart';class DefaultTextFormField extends StatelessWidget {  final String text;  final bool? expand;  final bool? title;  final TextInputType type;  final TextEditingController? controller;  final IconData? prefix;  final IconData? suffix;  final bool isPassword;  final Function()? pressSuffix;  final Function()? tap;  final String? Function(String?)? inputValidator;  final ValueChanged<String>? submitted;  const DefaultTextFormField({    super.key,    required this.text,    required this.type,    this.controller,    this.prefix,    this.suffix,    this.isPassword = false,    this.pressSuffix,    this.tap,    this.inputValidator,    this.submitted,    this.expand, this.title,  });  int? lines() {    if (expand == true) {      return null;    } else {      return 1;    }  }  InputBorder? border() {    if (expand == true||title==true) {      return null;    } else {      return OutlineInputBorder(borderRadius: BorderRadius.circular(20));    }  }  @override  Widget build(BuildContext context) {    return TextFormField(      style: Theme.of(context).textTheme.bodyMedium,      autocorrect: true,      autofocus: true,      expands: expand ?? false,      maxLines: lines(),      minLines: lines(),      onFieldSubmitted: submitted,      controller: controller,      onTap: tap,      keyboardType: type,      obscureText: isPassword,      validator: inputValidator,      decoration: InputDecoration(         alignLabelWithHint:true,        labelText: text,        prefixIcon: prefix != null ? Icon(prefix) : null,        suffixIcon: suffix != null            ? IconButton(                onPressed: pressSuffix,                icon: Icon(suffix),              )            : null,        border: border(),      ),    );  }}