import 'package:flutter/material.dart';import 'package:note_pulse/Features/Main/data/models/notes_model.dart';import 'package:note_pulse/Features/Main/presentation/manager/main_cubit.dart';import 'package:note_pulse/Features/Review/widgets/review_body.dart';class ReviewScreen extends StatefulWidget {  final NotesModel note;  const ReviewScreen({super.key, required this.note});  @override  State<ReviewScreen> createState() => _ReviewScreenState();}class _ReviewScreenState extends State<ReviewScreen> {  late bool isPassed;  @override  void initState() {    isPassed = MainCubit.get(context)        .isDateTimePassed(widget.note.date, widget.note.time);    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(),      body: ReviewBody(isPassed: isPassed, widget: widget),    );  }}