import 'package:flutter/material.dart';import '../../../Core/Resources/icon.dart';import '../review_screen.dart';class ReviewBody extends StatelessWidget {  const ReviewBody({    super.key,    required this.isPassed,    required this.widget,  });  final bool isPassed;  final ReviewScreen widget;  @override  Widget build(BuildContext context) {    return SafeArea(      child: SizedBox(        width: double.infinity,        child: Padding(          padding: const EdgeInsets.all(15.0),          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Row(                children: [                  Padding(                    padding: const EdgeInsets.all(15.0),                    child: Text(                      isPassed ? "Passed" : "Running",                      style: Theme.of(context).textTheme.bodySmall,                    ),                  ),                  if (widget.note.favorite == "yes") AppIcons.iconStar                ],              ),              Text(                widget.note.title,                style: Theme.of(context)                    .textTheme                    .bodyMedium!                    .copyWith(fontSize: 30, fontWeight: FontWeight.bold),              ),              const Divider(                height: 60,              ),              Text(                widget.note.field,                style: Theme.of(context)                    .textTheme                    .bodyMedium!                    .copyWith(fontSize: 20),              )            ],          ),        ),      ),    );  }}