import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../Main/presentation/manager/cubit.dart';import '../Main/presentation/manager/states.dart';class SettingScreen extends StatelessWidget {  const SettingScreen({super.key});  @override  Widget build(BuildContext context) {    return BlocConsumer<AppCubit, AppState>(      listener: (context, state) {        // TODO: implement listener      },      builder: (context, state) {        return Scaffold(          appBar: AppBar(),          body: Column(            children: [              ListTile(            title:  Text('Themes',style: Theme.of(context).textTheme.bodyMedium,),            trailing: Switch(                value: AppCubit                    .get(context)                    .dark,                onChanged: (value) => AppCubit.get(context).changeThemes(value)),          ),              ListTile(            title:  Text('Language',style: Theme.of(context).textTheme.bodyMedium),            trailing: Switch(                value: false,                onChanged: (value) {}),          ),            ],          ),        );      },    );  }}