import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../Main/presentation/manager/main_cubit.dart';import '../Main/presentation/manager/main_states.dart';class SettingScreen extends StatelessWidget {  const SettingScreen({super.key});  @override  Widget build(BuildContext context) {    return BlocConsumer<MainCubit, MainState>(      listener: (context, state) {        // TODO: implement listener      },      builder: (context, state) {        return Scaffold(          appBar: AppBar(),          body: Column(            children: [              ListTile(            title:  Text('Themes',style: Theme.of(context).textTheme.bodyMedium,),            trailing: Switch(                value: MainCubit                    .get(context)                    .dark,                onChanged: (value) => MainCubit.get(context).changeThemes(value)),          ),],          ),        );      },    );  }}